mapper.insert:
insert into orders (
    user_id, status, total_amount, created_at, updated_at
) values (
    #{userId}, #{status}, #{totalAmount}, #{createdAt}, #{updatedAt}
)

mapper.update:
update orders
status = #{status}, total_amount = #{totalAmount}, updated_at = #{updatedAt},
where order_id = #{orderId}

mapper.select:
select order_id, user_id, status, total_amount, created_at, updated_at
from orders
where user_id = #{userId}

    and status = #{status}

order by created_at desc

select count(*)
from orders
where user_id = #{userId}

    and status = #{status}

select order_id, user_id, status, total_amount, created_at, updated_at
from orders
where user_id = #{userId}

    and status = #{status}

order by created_at desc
limit #{offset}, #{limit}
